import { Component } from '@angular/core';

@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html',
  styleUrls: ['./productos.component.css']
})
export class ProductosComponent {

}
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Tabla de Productos</title>
    <style>
        body {
            font-family: Arial, sans-serif;
        }

        table {
            width: 100%;
            border-collapse: collapse;
            margin-bottom: 20px;
        }

        table, th, td {
            border: 1px solid #ddd;
        }

        th, td {
            padding: 12px;
            text-align: left;
        }

        th {
            background-color: #0099CC;
            color: #FFFFFF;
        }

        tr:nth-child(even) {
            background-color: #f2f2f2;
        }

        tr:hover {
            background-color: #66CCFF;
        }
    </style>
</head>
<body>
    <table>
        <thead>
            <tr>
                <th>ID</th>
                <th>Nombre</th>
                <th>Precio</th>
                <th>Precio con Tarjeta</th>
                <th>Categoría</th>
                <th>Descripción</th>
            </tr>
        </thead>
        <tbody>
            <tr>
                <td>1</td>
                <td>Producto 1</td>
                <td>$50.00</td>
                <td>$45.00</td>
                <td>Ropa</td>
                <td>Descripción del Producto 1</td>
            </tr>
            <tr>
                <td>2</td>
                <td>Producto 2</td>
                <td>$40.00</td>
                <td>$38.00</td>
                <td>Calzado</td>
                <td>Descripción del Producto 2</td>
            </tr>
            <!-- Agrega más filas para otros productos según sea necesario -->
        </tbody>
    </table>
</body>
</html>
